import React, { useState } from 'react';
import "./ExpenseForm.css";

const ExpenseForm = () => {

    const [title, setTitle] = useState('');
    const [amount, setAmount] = useState('');
    const [date, setDate] = useState('');

    function handeltitle(event) {
       setTitle(event.target.value);
    }

    function handelamount(event) {
        setAmount(event.target.value)
    }

    function handeldate(event) {
        setDate(event.target.value)
    }

    return <form>
        <div className="new-expense__controls">
            <div className="new-expense__controls">
                <label>Title</label>
                <input className="Exinput" type="text" onChange={handeltitle}/>
            </div>
            <div className="new-expense__controls">
                <label>Amount</label>
                <input className="Exinput" type="num" min="0.01" step="0.01" onChange={handelamount} />
            </div>
            <div className="new-expense__controls">
                <label>Date</label>
                <input className="Exinput" type="date" min="2020-01-01" max="2022-12-31" onChange={handeldate} />
            </div>
        </div>
        <div className="new-expense__actions">
            <button type="submit">Add Expense</button>
        </div>
    </form>
};

export default ExpenseForm;
