import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";
import "./Expenses.css";

const Expenses = (props) => {
  const [filteredDate, setfilteredDate] = useState("2020");

  const handeldateevent = (date) => {
    setfilteredDate(date);
  };

  const filteredExpenses = props.items.filter((expenses) => {
    return expenses.date.getFullYear().toString() === filteredDate;
  });

  console.log(filteredExpenses);

  let expensesContent = <p>Uhh! No Expenses found.</p>;

  if (filteredExpenses.length > 0) {
    expensesContent = filteredExpenses.map((expense) => (
      <ExpenseItem
        key={expense.id}
        title={expense.title}
        amount={expense.amount}
        date={expense.date}
      />
    ));
  }

  return (
    <div className="exp-div-main">
      <ExpenseFilter
        selected={filteredDate}
        onSaveFilterDate={handeldateevent}
      />
      {expensesContent}
    </div>
  );
};

export default Expenses;
