import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";
import "./Expenses.css";

const Expenses = (props) => {
  const [filteredDate, setfilteredDate] = useState("2020");

  const handeldateevent = (date) => {
    setfilteredDate(date);
  };

  const filteredExpenses = props.items.filter((expenses) => {
    return expenses.date.getFullYear().toString() === filteredDate;
  });

  console.log(filteredExpenses);

  return (
    <div className="exp-div-main">
      {/* <Card className="expenses"> */}
      <ExpenseFilter
        selected={filteredDate}
        onSaveFilterDate={handeldateevent}
      />
      {filteredExpenses.length === 0 ? (
        <p>Uhh! No Expenses found.</p>
      ) : (
        filteredExpenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))
      )}

      {/* <ExpenseItem
          title={props.items[0].title}
          amount={props.items[0].amount}
          date={props.items[0].date}
        />
        <ExpenseItem
          title={props.items[1].title}
          amount={props.items[1].amount}
          date={props.items[1].date}
        />
        <ExpenseItem
          title={props.items[2].title}
          amount={props.items[2].amount}
          date={props.items[2].date}
        />
        <ExpenseItem
          title={props.items[3].title}
          amount={props.items[3].amount}
          date={props.items[3].date}
        /> */}
      {/* </Card> */}
    </div>
  );
};

export default Expenses;
