import React, {useState} from 'react';
import ExpenseItem from "./components/ExpenseItem/ExpenseItem"
import expenses from "./expense";
import NewExpense from "./components/NewExpense/AddExpense.jsx"

const App = () => {

  const [exp, setExp] = useState(expenses);

  const addExpenseHandel = expense => {
    setExp()
  }

  return (
    <div>
    <NewExpense onAddExpense={addExpenseHandel}/>
      {expenses.map( exp =>  {
        return (
          <ExpenseItem key={exp.id} title={exp.title} amount={exp.amount} date={exp.date}/>
        );
      })}
    </div>
  );
}

export default App;
